mapper.select:
select t1.id,t1.device_id,t1.divisor_id,t2.`name` as divisor_name,t2.`code` as divisor_code from analog_code t1
left join sys_divisor t2 on t1.divisor_id =t2.id
where t1.device_id=#{deviceId}

select t1.id,t1.device_id,t1.divisor_id,t2.`name` as divisor_name,t2.`code` as divisor_code from analog_code t1
left join sys_divisor t2 on t1.divisor_id =t2.id
where t2.name=#{divisorName}
